import React, { useState } from "react";
import Select from "react-select";

interface Props {
  onApplyFilters: (filters: {
    keywords: string;
    language: string;
    minGoodFirstIssues: number;
    maxGoodFirstIssues: number;
    topics: string[];
    recentCommitDays: number;
  }) => void;
}

const topicsOptions = [
  { value: "machine-learning", label: "Machine Learning" },
  { value: "web", label: "Web" },
  { value: "cli", label: "CLI" },
  // ...add 100+ topics here...
];

const FilterPanel: React.FC<Props> = ({ onApplyFilters }) => {
  const [keywords, setKeywords] = useState("");
  const [language, setLanguage] = useState("");
  const [minGoodFirstIssues, setMinGoodFirstIssues] = useState(0);
  const [maxGoodFirstIssues, setMaxGoodFirstIssues] = useState(1000);
  const [selectedTopics, setSelectedTopics] = useState<{ value:string; label:string }[]>([]);
  const [recentCommitDays, setRecentCommitDays] = useState(90);

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    onApplyFilters({
      keywords,
      language,
      minGoodFirstIssues,
      maxGoodFirstIssues,
      topics: selectedTopics.map(t => t.value),
      recentCommitDays,
    });
  };

  return (
    <form onSubmit={handleSubmit} style={{ marginBottom: 20 }}>
      {/* Other inputs unchanged */}

      <div>
        <label>Topics: </label>
        <Select
          isMulti
          options={topicsOptions}
          value={selectedTopics}
          onChange={(selected) => setSelectedTopics(selected as any)}
          placeholder="Select topics..."
          closeMenuOnSelect={false}
          isSearchable
        />
      </div>

      <button style={{ marginTop: 10 }} type="submit">Search</button>
    </form>
  );
};

export default FilterPanel;
