import React, { useState } from "react";
import Select from "react-select";

interface Props {
  onApplyFilters: (filters: {
    keywords: string;
    language: string;
    minGoodFirstIssues: number;
    maxGoodFirstIssues: number;
    topics: string[];
    recentCommitDays: number;
  }) => void;
}

const topicsOptions = [
  { value: "machine-learning", label: "Machine Learning" },
  { value: "web", label: "Web" },
  { value: "cli", label: "CLI" },
  // Add more 100+ topics here
];

const FilterPanel: React.FC<Props> = ({ onApplyFilters }) => {
  const [keywords, setKeywords] = useState("");
  const [language, setLanguage] = useState("");
  const [minGoodFirstIssues, setMinGoodFirstIssues] = useState(0);
  const [maxGoodFirstIssues, setMaxGoodFirstIssues] = useState(1000);
  const [selectedTopics, setSelectedTopics] = useState<{ value:string; label:string }[]>([]);
  const [recentCommitDays, setRecentCommitDays] = useState(90);

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    onApplyFilters({
      keywords,
      language,
      minGoodFirstIssues,
      maxGoodFirstIssues,
      topics: selectedTopics.map(t => t.value),
      recentCommitDays,
    });
  };

  return (
    <form onSubmit={handleSubmit} style={{ marginBottom: 20 }}>
      <div>
        <label>Keywords: </label>
        <input
          type="text"
          value={keywords}
          onChange={(e) => setKeywords(e.target.value)}
          placeholder="Search keywords"
        />
      </div>

      <div>
        <label>Language: </label>
        <input
          type="text"
          value={language}
          onChange={(e) => setLanguage(e.target.value)}
          placeholder="e.g. Python, JavaScript"
        />
      </div>

      <div>
        <label>Good First Issues (min): </label>
        <input
          type="number"
          min={0}
          value={minGoodFirstIssues}
          onChange={(e) => setMinGoodFirstIssues(Number(e.target.value))}
        />
      </div>

      <div>
        <label>Good First Issues (max): </label>
        <input
          type="number"
          min={0}
          value={maxGoodFirstIssues}
          onChange={(e) => setMaxGoodFirstIssues(Number(e.target.value))}
        />
      </div>

      <div>
        <label>Topics: </label>
        <Select
          isMulti
          options={topicsOptions}
          value={selectedTopics}
          onChange={(selected) => setSelectedTopics(selected as any)}
          placeholder="Select topics..."
          closeMenuOnSelect={false}
          isSearchable
        />
      </div>

      <div>
        <label>Commit Recency (days): </label>
        <input
          type="number"
          min={1}
          max={365}
          value={recentCommitDays}
          onChange={(e) => setRecentCommitDays(Number(e.target.value))}
        />
      </div>

      <button type="submit" style={{ marginTop: 10 }}>
        Search
      </button>
    </form>
  );
};

export default FilterPanel;
