import React, { useState } from "react";

export type Filters = {
  keywords: string;
  language: string;
  topics: string[];
  minGoodFirstIssues: number;
  maxGoodFirstIssues: number;
  recentCommitDays: number;
};

type FilterPanelProps = {
  filters: Filters;
  languageOptions: string[];
  topicOptions: string[];
  onFiltersChange: (filters: Filters) => void;
  onSearchClick: (filters: Filters) => void;
};

const FilterPanel: React.FC<FilterPanelProps> = ({ filters, onFiltersChange, languageOptions, topicOptions, onSearchClick }) => {
  const [localKeywords, setLocalKeywords] = useState(filters.keywords);

  const handleSearchClick = () => {
    onFiltersChange({ ...filters, keywords: localKeywords });
    onSearchClick({ ...filters, keywords: localKeywords });
  };

  const handleTopicChange = (e: React.ChangeEvent<HTMLSelectElement>) => {
    const selected = Array.from(e.target.selectedOptions, (option) => option.value);
    onFiltersChange({ ...filters, topics: selected });
  };

  return (
    <div className="filter-panel centered-panel">
      <input
        type="text"
        placeholder="Enter keywords"
        value={localKeywords}
        onChange={(e) => setLocalKeywords(e.target.value)}
        className="filter-input rounded-input"
      />
      <button className="search-button rounded-input" onClick={handleSearchClick}>
        Search
      </button>

      <select
        className="filter-select rounded-input"
        value={filters.language}
        onChange={(e) => onFiltersChange({ ...filters, language: e.target.value })}
      >
        <option value="">All Languages</option>
        {languageOptions.map((lang) => (
          <option key={lang} value={lang}>
            {lang}
          </option>
        ))}
      </select>

      <select
        className="filter-select multi-select rounded-input"
        multiple
        size={Math.min(6, topicOptions.length)}
        value={filters.topics}
        onChange={handleTopicChange}
      >
        {topicOptions.map((topic) => (
          <option key={topic} value={topic}>
            {topic}
          </option>
        ))}
      </select>

      <select
        className="filter-select rounded-input"
        value={filters.minGoodFirstIssues}
        onChange={(e) => onFiltersChange({ ...filters, minGoodFirstIssues: Number(e.target.value) })}
      >
        {[0, 1, 2, 3, 4, 5].map((v) => (
          <option key={v} value={v}>
            {v}+
          </option>
        ))}
      </select>

      <select
        className="filter-select rounded-input"
        value={filters.maxGoodFirstIssues}
        onChange={(e) => onFiltersChange({ ...filters, maxGoodFirstIssues: Number(e.target.value) })}
      >
        {[10, 50, 100, 500, 1000].map((v) => (
          <option key={v} value={v}>
            {v}
          </option>
        ))}
      </select>

      <select
        className="filter-select rounded-input"
        value={filters.recentCommitDays}
        onChange={(e) => onFiltersChange({ ...filters, recentCommitDays: Number(e.target.value) })}
      >
        {[30, 60, 90, 180].map((v) => (
          <option key={v} value={v}>
            Last {v} days
          </option>
        ))}
      </select>
    </div>
  );
};

export default FilterPanel;
