import React, { useState } from "react";
import FilterPanel from "../components/FilterPanel";
import ResultsList from "../components/ResultsList";
import LoadingShuffledCards from "../components/LoadingShuffledCards"; // optional loading component
import { RepoScoreResult } from "../types/RepoScoreResult";
import { fetchReposWithScores } from "../services/api";

const MainPage: React.FC = () => {
  const [repos, setRepos] = useState<RepoScoreResult[]>([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState<string | null>(null);

  const handleSearch = async (filters: {
    keywords: string;
    language: string;
    minGoodFirstIssues: number;
    maxGoodFirstIssues: number;
    topics: string[];
    recentCommitDays: number;
  }) => {
    setLoading(true);
    setError(null);
    setRepos([]);

    try {
      const data = await fetchReposWithScores(filters);
      setRepos(data);
    } catch (e) {
      setError("Failed to fetch data. Please try again.");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div style={{ maxWidth: 900, margin: "auto", padding: 20 }}>
      <h1>Repository Discoverability & Scoring</h1>
      <FilterPanel onApplyFilters={handleSearch} />
      {loading && <LoadingShuffledCards />}
      {error && <p style={{ color: "red" }}>{error}</p>}
      {!loading && !error && <ResultsList repos={repos} />}
    </div>
  );
};

export default MainPage;
